INSERT INTO `wp_posts` VALUES ('130', '1', '2010-03-24 12:19:32', '2010-03-24 12:19:32', '<strong>Judging panel</strong>\r\n\r\nPic of each of judges with short biog for each (celeb figurehead, journo x 2, raindance, Alex)\r\n\r\n<strong>Prizes</strong>\r\n\r\n<strong>First Prize</strong>\r\n<ol>\r\n	<li>A trip to the Cannes Film Festival 2011 will be awarded for the best work as judged by the judging panel.</li>\r\n	<li>The winning film will also be shown at the Raindance Film Festival 2010</li>\r\n	<li>The winning film will be promoted by InterCasino</li>\r\n	<li>Winner will receive a free subscription for one year to Raindance.tv and a full Raindance Festival Pass</li>\r\n</ol>\r\n<strong>YouTube prize </strong>[Please note that Raindance.tv will syndicate your entry to the official page on You Tube on your behalf]\r\n<ol>\r\n	<li>The film that gets the highest number of hits on      the competition’s YouTube channel (where all shortlisted entries will be      available) will receive access      to the British Independent Film Awards (BIFA).</li>\r\n	<li>Winner will receive a free subscription for one      year to Raindance.tv and a full Raindance Festival Pass</li>\r\n</ol>\r\n<strong>Shortlisted Films</strong>\r\n<ol>\r\n	<li>Shortlisted films will receive complementary      Raindance training courses</li>\r\n	<li>All shortlisted films will be showcased on      Raindance, YouTube and the InterCasino site</li>\r\n	<li>All films will be considered for screening at the      Raindance Film Festival</li>\r\n</ol>', 'Judging & Prizes', '', 'inherit', 'open', 'open', '', '50-revision-8', '', '', '2010-03-24 12:19:32', '2010-03-24 12:19:32', '', '50', 'http://webtrendz.co.uk/extraordinary/2010/03/50-revision-8/', '0', 'revision', '', '0');
