INSERT INTO `wp_posts` VALUES ('201', '1', '2010-05-02 09:30:02', '2010-05-02 09:30:02', '<strong>Prizes</strong>\r\n\r\n<strong>First Prize</strong>\r\n<ol>\r\n	<li>A trip to the Cannes Film Festival      2011 will be awarded for the best work as judged by the judging panel.</li>\r\n	<li>The winning film will also be shown      at the Raindance Film Festival 2010</li>\r\n	<li>Winner will receive a free      subscription for one year to Raindance       and a full Raindance Festival Pass</li>\r\n	<li>The winning film will be promoted by      InterCasino</li>\r\n</ol>\r\n<strong>YouTube prize </strong>[Please note that Raindance.tv will syndicate your entry to the official page on You Tube on your behalf]\r\n<ol>\r\n	<li>The film that gets the highest number      of views on the competition’s YouTube channel (where all shortlisted      entries will be available) will receive two tickets to the British Independent      Film Awards (BIFA) after-show party.</li>\r\n	<li>Winner will receive a free subscription for one      year to Raindance.tv and a full Raindance Festival Pass</li>\r\n</ol>\r\n<strong>Shortlisted Films</strong>\r\n<ol>\r\n	<li>Shortlisted films will receive a free      one year membership to Raindance</li>\r\n	<li>All shortlisted films will be      showcased on Raindance site, YouTube and the InterCasino site</li>\r\n	<li>All films will be considered for      screening at the Raindance Film Festival</li>\r\n</ol>', 'The Prizes', '', 'trash', 'closed', 'closed', '', 'the-prizes', '', '', '2010-05-21 15:13:13', '2010-05-21 15:13:13', '', '61', 'http://webtrendz.co.uk/extraordinary/?page_id=201', '2', 'page', '', '0');
